import { FC, useState } from 'react';
import './pagination.scss';
import 'antd/dist/antd.css';
import { Pagination } from 'antd';
import { useAppSelector } from '../../hooks/redux';
import { requestDataSlice } from '../../store/Reducers/RequestDataSlice';

const Pagin: FC = () => {
  const { setActivePage } = requestDataSlice.actions;
  const [current, setCurrent] = useState(3);

  const { ActivePage: page } = useAppSelector((state) => state.requestDataReducer);

  const switchPage = (page: number) => {
    console.log(page);
    setCurrent(page);
    setActivePage(page);
  };
  return (
    <div className="pagination">
      <Pagination current={current} onChange={switchPage} total={50} />
    </div>
  );
};

export default Pagin;
