import { FC } from 'react';
import { IPost } from '../../models/Post';
import './post.scss';

const Post: FC<IPost> = (data) => {
  return (
    <div className="block">
      <div className="title">
        <h1>{data.title}</h1>
        <label>by: {data.name}</label>
      </div>
      <span className="text">{data.body}</span>
    </div>
  );
};

export default Post;
